import React, {useState} from 'react';
import styled from 'styled-components';
import Button from './Button';
import { useExpenitureDispatch, useExpenitureNextId } from './ExpenditureContext';


const DarkBackground = styled.div`
  position: fixed;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  /* 세로 좌우 정렬 */
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(0,0,0,0.8);
`
const DialogLayout = styled.div`
  width: 270px;
  padding: 1.5rem;
  background: #fff;
  border-radius: 5px;

  h3 {
    margin: 0;
    font-size: 1.3rem;
    font-weight: bold;
    margin-bottom: 10px;
  }

  `
const DialogBlock = styled.div`
  padding: 10px;

  h4 {
    margin: 0;
    margin-bottom: 5px;
    font-size: 1rem;
    font-weight: bold;
  }

  input {
    height: 20px;
    width: 90%;
    margin-bottom: 20px;
    border-radius: 5px;
    border: 2px solid #ced4da;
  }

  select {
    height: 27px;
    width: 92%;
    border-radius: 5px;
    border: 2px solid #ced4da;
  }

`
const ButtonGroup = styled.div`
  margin-top: 1.5rem;
  display: flex;
  justify-content: flex-end;

  Button {
    margin-right: 10px;
    border-radius: 5px;
    font-size: 0.9rem;
    padding: 5px 10px;
}
`

function Dialog({
  text,
  // children,
  confirmText,
  cancelText,
}) 
{
  const nextId = useExpenitureNextId();
  const dispatch = useExpenitureDispatch(); 
  const onClick = e => {
    dispatch({
      type: 'CREATE',
      item: {
        id: nextId.current,
        title: '병원 진료',
        category: '', // 임의로 원하는 카테고리를 지정해두기
        amount: '',
      }
    })
    onReset()
    nextId.current += 1;
  }

  const [inputs, setInputs] = useState({
    title: '',
    amount: '',
  })
 
  const onChange = e => {
    const {name, value} = e.target;

    setInputs({
      ...inputs,
      [name]: value,
    })
  }

  const onSelect = e => {

  }

  const onReset = () => {
    setInputs({
      ...inputs,
      title: '',
      amount: '',
    })
  }

  return (
    <DarkBackground>
      <DialogLayout>
        <h3>{text}</h3>
        <DialogBlock>
          <h4>내용</h4>
          <input name="title" onChange={onChange} value={inputs.title}/>
          <h4>금액</h4>
          <input name="amount" onChange={onChange} value={inputs.amount}/>
          <h4>카테고리</h4>
          <select onChange={onSelect}>
          <option value="식사">식사</option>
          <option value="식료품">식료품</option>
          <option value="교통">교통</option>
          <option value="생활">생활</option>
          <option value="의료">의료</option>
          </select>
        </DialogBlock>
        <ButtonGroup>
          <Button color='gray' onClick={onClick}> 
            {cancelText}
          </Button>
          <Button color='blue'>
            {confirmText}
          </Button>
        </ButtonGroup>
      </DialogLayout>
    </DarkBackground>
  )
}

Dialog.defaultProps = {
  cancelText: ' 취소',
  confirmText: ' 확인',
}

export default Dialog
