import React, { useState} from 'react';
import { createGlobalStyle, ThemeProvider } from 'styled-components';
import ExpenditureTemplate from './component/ExpenditureTemplate';
import ExpenditureHead from './component/ExpenditureHead';
import ExpenditureFilter from './component/ExpenditureFilter';
import ExpenditureList from './component/ExpenditureList';
import ExpenditureCreate from './component/ExpenditureCreate';
import Dialog from './component/Dialog'

const palette = {
  blue: '#4c6ef5',
  gray: '#495057',
  pink: '#e64980',
  yellow: '#ffec99',
  lime: '#d8f5a2',
  orange:'#ffd8a8',
  purple: '#eebefa',
}

const GlobalStyle = createGlobalStyle`
  body {
    background: #e9ecef;
  }
`
const initial = [
  {
    id: 1,
    title: '용개반점',
    category: '식사', // 임의로 원하는 카테고리를 지정해두기
    amount: 7000
  },
  {
    id: 2,
    title: '양배추',
    category: '식료품', // 임의로 원하는 카테고리를 지정해두기
    amount: 5000
  },
  {
    id: 3,
    title: '택시비',
    category: '교통', // 임의로 원하는 카테고리를 지정해두기
    amount: 20000
  },
  {
    id: 4,
    title: '관리비',
    category: '생활', // 임의로 원하는 카테고리를 지정해두기
    amount: 100000
  },
  {
    id: 5,
    title: '병원 진료',
    category: '의료', // 임의로 원하는 카테고리를 지정해두기
    amount: 7000
  },
]

function App() {
  const [dialog, setDialog] = useState(false);
  const onClick = () => setDialog(true);

  return (
    <>
    <ThemeProvider theme={{palette}}>
      <GlobalStyle />
      <ExpenditureTemplate>
        <ExpenditureHead></ExpenditureHead>
        <ExpenditureFilter></ExpenditureFilter>
        <ExpenditureList></ExpenditureList>
        <ExpenditureCreate onClick={onClick}>
        </ExpenditureCreate>
        {dialog && (
          <Dialog title="지출 등록" confirmText="등록" cancelText="취소"></Dialog>
        )}
      </ExpenditureTemplate>
    </ThemeProvider>
    </>
  );
}

export default App;
