import React from 'react';
import styled from 'styled-components';
import Button from './Button';

const DarkBackground = styled.div`
  position: fixed;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  /* 세로 좌우 정렬 */
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(0,0,0,0.8);
`

const DialogBlock = styled.div`
  width: 320px;
  padding: 1.5rem;
  background: #fff;
  border-radius: 2px;

  h3 {
    margin: 0;
    font-size: 1.2rem;
  }

  h4 {
    margin: 0;
    font-size: 1rem;
  }

  p {
    font-size: 1.125rem;
  }

  input {
    width: 90%;
  }
`
const ButtonGroup = styled.div`
  margin-top: 3rem;
  display: flex;
  justify-content: flex-end;
`

function Dialog({
  title,
  children,
  confirmText,
  cancelText,
}) {
  return (
    <DarkBackground>
      <DialogBlock>
        <h3>{title}</h3>
        <p>{children}</p>
        <ButtonGroup>
          <Button color="gray">
            {cancelText}
          </Button>
          <Button color="blue">
            {confirmText}
          </Button>
        </ButtonGroup>
      </DialogBlock>
    </DarkBackground>
  )
}

Dialog.defaultProps = {
  cancelText: ' 취소',
  confirmText: ' 확인',
}

export default Dialog;
