import React from 'react';
import styled, { css } from 'styled-components';
import {darken, lighten} from 'polished';

const StyledButton = styled.button`
  /* display: inline-flex; */
  outline:none;
  border:none;
  color:#fff;
  font-weight: bold;
  cursor: pointer;
  padding-left: 1rem;
  padding-right: 1rem;

  /* 크기 */
  height: 2.25rem;
  font-size: 1rem;

  /* 색상 */
  background: #444;
  &:hover {
    background: ${props => lighten(0.1, props.theme.palette.gray)};
  &:active {
    background: ${darken(0.1, '#444')};

    /* 기타 */
  & + & {
    margin-left: 1rem;
  }
`;

function Button({ children, ...rest}) {
  return (
    <StyledButton{...rest}>
    {children}
    </StyledButton>

  )
}

export default Button;
