  padding-left: 32px;
  padding-right: 32px;
  padding-bottom: 24px;
  border-bottom: 1px slid #e9ecef;

  h1 {
    margin: 0;
    font-size: 36px;
    color: #343a40;
  }

  .day {
    margin-top: 4px;
    color: #868e96;
    font-size: 21px;
  }

  .tasks-left {
    color: #20c997;
    font-size: 18px;
    margin-top: 40px;
    font-weight: bold;
  }
`;

function ExpenditureHead() {
  // const ExpenditureHead = useTodoState();
  // const undoneTasks = todos.filter(todo => !todo.done)

  const today = new Date();
  const dateString = today.toLocaleDateString('ko-KR', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  })
  const dayName = today.toLocaleDateString('ko-KR', {
    weekday: 'long'
  })
  return (
   <TodoHeadBlock>
     <h1>오늘의 지출</h1>
     {/* <div className="day">{dayName}</div>
     <div className="tasks-left">할일 {undoneTasks.length }개 남음</div> */}
   </TodoHeadBlock>
  )
}
export default ExpenditureHead;
