import React from 'react';
import styled from 'styled-components';
import { MdModeEdit, MdDelete } from 'react-icons/md';
import { useExpenitureDispatch } from './ExpenditureContext';

const Remove = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size:24px;
  cursor: pointer;
  `
const Edit = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size:24px;
  margin-right: 10px;
  cursor: pointer;
`
const Cost = styled.div`
  font-size: 14px;
  font-weight: bold;
  color: #c92a2a;
  margin-right: 10px;
`

const Contents = styled.div`
  font-size: 16px;
  font-weight: bold;
  /* margin-right: 100px; */
`
const FilterItem = styled.div`
  background: #f3d9fa;
  padding: 10px;
  border-radius: 5px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 16px;
  font-weight: bold;
  /* margin-right: 10px; */
`
const ExpenditureItemBlock = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding-top:12px;
  padding-bottom: 12px;
  border: 1px solid brown; 
  margin-bottom: -10px;

`

function ExpenditureItem({id, category, title, amount}) {
  const dispatch = useExpenitureDispatch();
  // dispatch {
  //   type:
  // }
  const onRemove = () =>
    dispatch({
      type: 'REMOVE',
      id
    })
  return (
    <ExpenditureItemBlock>
     <FilterItem>{category}</FilterItem>
     <Contents>{title}</Contents>
     <Cost>{amount}</Cost>
     <Edit>
       <MdModeEdit />
     </Edit>
     <Remove>
       <MdDelete />
     </Remove>
    </ExpenditureItemBlock>
  )
}
export default ExpenditureItem;
