// @flow
import React from 'react';
import styled from 'styled-components';
// import { useTodoState } from './TodoContext';

const ExpenditureHeadBlock = styled.div`
  padding-top: 38px;
  padding-left: 32px;
  padding-right: 32px;
  padding-bottom: 24px;
  border-bottom: 1px slid #e9ecef;

  h1 {
    margin-bottom: 15px;
    font-size: 28px;
    color: #343a40;
  }

  .day {
    margin-bottom: 15px;
    color: #343a40;
    /* font-weight: 800; */
    font-weight: bold;
    font-size: 18px;
  }

  .total-expenditure {
    margin-bottom: 15px;
    color: #343a40;
    font-size: 18px;
    font-weight: bold;
  }
  .highlighter {
    margin-left: 5px;
    color: red;
  }
`;

function ExpenditureHead() {
  // const ExpenditureHead = useTodoState();
  // const undoneTasks = todos.filter(todo => !todo.done)

  const today = new Date();
  const dateString = today.toLocaleDateString('ko-KR', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  })
  const dayName = today.toLocaleDateString('ko-KR', {
    weekday: 'long'
  })
  return (
   <ExpenditureHeadBlock>
     <h1>오늘의 지출</h1>
     <div className="day">{dateString}</div>
     <div className="total-expenditure">총 지출:<sapn className="highlighter">100000원</sapn></div>
   </ExpenditureHeadBlock>
  )
}
export default ExpenditureHead;
