import React, { useState } from 'react';
import styled, {css} from 'styled-components';
import { MdModeEdit, MdDelete } from 'react-icons/md';
import { useExpenitureDispatch } from './ExpenditureContext';
import Dialog from './Dialog'

const Remove = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size:24px;
  cursor: pointer;
  `
const Edit = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size:24px;
  cursor: pointer;
`
const Cost = styled.div`
  font-size: 16px;
  font-weight: bold;
  color: #c92a2a;
`

const Contents = styled.div`
  font-size: 16px;
  font-weight: bold;

`

const categoryPalette = {
  식사: 'yellow',
  식료품: 'lime',
  교통: 'orange',
  생활: 'purple',
  의료: 'lightblue',
};

const FilterItem = styled.div`
  padding: 10px;
  border-radius: 5px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 16px;
  font-weight: bold;

  ${({ theme, category }) => {
    const color = categoryPalette[category];

    return css`
      ${category &&
        css`
          background: ${theme.palette[color]};
        `};
    `;
  }}
  /* margin-right: 10px; */
`
const ExpenditureItemBlock = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding-top:12px;
  padding-bottom: 12px;
  margin-bottom: -10px;

`

function ExpenditureItem({id, category, title, amount}) {
  const dispatch = useExpenitureDispatch();

  const [open, setOpen] = useState(false);

  const onOpen = () => setOpen(!open);
  const onCancel = () => setOpen(false);

  const onRemove = () =>
    dispatch({
      type: 'REMOVE',
      id
    })
  return (
    <>
    <ExpenditureItemBlock>
     <FilterItem category={category}>{category}</FilterItem>
     <Contents>{title}</Contents>
     <Cost>{amount}</Cost>
     <Edit>
       <MdModeEdit />
     </Edit>
     <Remove onClick={onOpen}>
       <MdDelete />
     </Remove>
    </ExpenditureItemBlock>
     {open && (
     <Dialog
      text="정말 삭제 하시겠습니까?"
      confirmText="삭제"
      confirmColor="pink"
      cancelColor="gray"
      onConfirm={onRemove}
      onCancel={onCancel}
     >
     </Dialog>
     )}
    </> 
  )
}
export default React.memo(ExpenditureItem);
