import React, {useState} from 'react';
import styled, { css } from 'styled-components';
import { MdAdd } from 'react-icons/md';
import { useExpenitureDispatch, useExpenitureNextId } from './ExpenditureContext';
import Dialog from './Dialog'

const AddButton = styled.button`
  background: ${props => props.theme.palette.gray};
  /* &:hover {
    background:${props => props.theme.palette.yellow};
  }
  &:active {
    background: ${props => props.theme.palette.yellow};
  } */
  cursor: pointer; 
  width: 50px;
  height: 50px;
  display: flex; 
  align-items: center;
  margin: 20px;

  font-size: 60px;
  color: white;
  border-radius: 40px;

  border: none;
  outline: none;

  /* transition: 0.125s all ease-in;
  ${props => 
    props.open && 
    css`
      background: #ff6b6b;
      &:hover {
        background: #ff8787;
      }
      &:active {
        background: #fa5252;
      }
      transform: rotate(45deg);
    `} */
`

function ExpenditureCreate() {
  const nextId = useExpenitureNextId();
  const dispatch = useExpenitureDispatch(); 

  const [dialog, setDialog] = useState(false);
  const open = () => setDialog(true);
  // const [open, setOpen] = useState(false);
  // const onClick = () => setOpen(!open);
  const onClick = () => {
    dispatch({
      type: 'CREATE',
      item: {
        id: 6,
        title: '병원 진료',
        category: '', // 임의로 원하는 카테고리를 지정해두기
        amount: '',
      }
    })
  }
 return (
  <>
   <AddButton onClick={open}>
     <MdAdd />
   </AddButton>
   {dialog && (
     <Dialog></Dialog>
   )}
  </>
 )
}
export default ExpenditureCreate;
